const fs = require('fs');
const path = require('path');
const jade = require('jade');
const inlineCss = require('inline-css');

const inlineCssOptions = {
	preserveMediaQueries: true,
	removeHtmlSelectors: true,
	url: '/',
};

module.exports = {
	cijfer: function (options) {
		options.insufficient = options.passed ? '' : 'insufficient';

		const html = jade.renderFile(path.join(__dirname, 'cijfer.jade'), options);
		return inlineCss(html, inlineCssOptions);
	},
	project: function (options) {
		const html = jade.renderFile(path.join(__dirname, 'project.jade'), options);
		return inlineCss(html, inlineCssOptions);
	},
	html: function (options) {
		const html = jade.renderFile(path.join(__dirname, 'html.jade'), options);
		return inlineCss(html, inlineCssOptions);
	}
};
